    public static int leftMostColumnWithOne(BinaryMatrix binaryMatrix) {
        List<Integer> binaryMatrixSize = binaryMatrix.dimensions();
        int width = binaryMatrixSize.get(0), length = binaryMatrixSize.get(1);
        int positionOfOne = Integer.MAX_VALUE;
        for (int i = 0; i < width; i++) {
            positionOfOne = Math.min(positionOfOne,
                    findFirstOne(binaryMatrix, i, 0, Math.min(positionOfOne,length - 1)));
        }
        return (positionOfOne == Integer.MAX_VALUE) ? -1 : positionOfOne;
    }

    public static int findFirstOne(BinaryMatrix binaryMatrix, int i, int left, int right) {
        int middle = (left + right) / 2;
        int valueInMiddle = binaryMatrix.get(i, middle);
        if (left == right) return (valueInMiddle == 1) ? left : Integer.MAX_VALUE;
        if (valueInMiddle == 1 && middle >= 1 && binaryMatrix.get(i, middle - 1) == 0) return middle;
        if (valueInMiddle == 1 && middle == 0) return middle;
        return (valueInMiddle == 0) ? findFirstOne(binaryMatrix, i, middle + 1, right) :
                findFirstOne(binaryMatrix, i, left, middle - 1);
    }

    public static void main(String[] args) {
        BinaryMatrix b = new BinaryMatrix();
        System.out.println(leftMostColumnWithOne(b));
        System.out.println(b.cols);
    }
